%Diagonalization Of A Matrix%Check whether the given matrix is diagonalizable or not.A=[1 6 1; 1 2 0;0 0 3][P,val]=eig(A)n=size(A,1)lambda=zeros(n,1)count=0; for i=1:n    lambda(i)=val(i,i)    for j=1:n        if(i~=j)                 if(val(i,i)==val(j,j))                         count=count+1                end         end    end endif(count==0)    disp('The Matrix is diagonalizable' )else     disp('The Matrix is not diagonalizable')end  if(det(P)~=0)    Pi=inv(P)end D=Pi*A*P