%To prove that Eigenvectors corresponding to different eigenvalues of a Hermitian matrix are orthogonal.c=complex(0,-2)d=complex(0,2)A=[-1 0 c;0 2 0;d 0 -1][P,vec]=eig(A)%Check if the matrix is a Hermitian matrixAdag=conj(A.')n=size(A,1)D=zeros(n,n)if(Adag==A)   disp('The Matrix is a Hermitian Matrix')   %Now taking the different eigen vectors separately   %Checking if the product of the eigenvectors individually is 0   %This will verify that the matrix is Orthogonal    for i=1:size(P,2)       for j=1:size(P,2)           if i~=j%It will give zero for which i!=j                d(i,j)=(conj(P(:,i)).')*P(:,j)                disp(d(i,j))         end     end    end else    disp('The Matrix is not a Hermitian Matrix') end if(D==zeros(n,n))     disp('The EigenVectors are orthogonal')end