%To multiply two given matricesA=[1 2 3;4 5 6]disp(A,"First matrix is")B=[1 2; 3 4;5 6]disp(B,"Second matrix is")[a,b]=size(A)[c,d]=size(B)if b==c then       for i=1:a        for j=1:d            sum=0            for k=1:b                sum=sum+A(i,k)*B(k,j)            end        end     end    C(i,j)=sum    disp(C,"Product of the given matrices is -")    else disp("Matrix multiplication is not possible")enddisp(A*B,"Result from direct command")